import React, { Component } from "react";

class Postform extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      body: ""
    };
  }
  render() {
    return (
      <div>
        <h1>Add Posts</h1>
        <form>
          <div>
            <label>Title: </label>
            <br />
            <input type="text" name="title" value={this.state.title} />
          </div>
          <div>
            <label>Body: </label>
            <br />
            <textarea name="body" />
          </div>
          <br />
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}
export default Postform;
